void update_wave(wave *w) {
    static float dt = 1.0 / 8000.0;
    w->time += dt;
    if (w->time >= w->period) {
        w->time -= w->period;
    }
    float phase = w->time * g_omega;
    switch (w->wave_type) {
        case 's':
            w->output = sine_wave(phase);
            break;
        case 't':
            w->output = triangle_wave(phase);
            break;
        case 'q':
            w->output = square_wave(phase);
            break;
        case 'a':
            w->output = sawtooth_wave(phase);
            break;
    }
}

