// VGA related functions
void update_x_y(int* x, int* y, int* last_x, int* last_y, int* last_last_x, int* last_last_y, int* dx, int* dy) {
    *last_last_y = *last_y;
    *last_y = *y;
    if (*y == 239 - 50 || *y == 0) {
        *dy = -*dy;
    }
    *y += *dy;

    *last_last_x = *last_x;
    *last_x = *x;
    if (*x == 319 - 50 || *x == 0) {
        *dx = -*dx;
    }
    *x += *dx;
}

