short int rgb_to_16bit(int r, int g, int b) {
    short int short_r = r * 31 / 255;
    short int short_g = g * 63 / 255;
    short int short_b = b * 31 / 255;

    return (short_r << 11) + (short_g << 5) + short_b;
}

